// gridlist[key].path

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // error: false,
      imgList: {},
      imgSizes: {},
      screenWidth: 1140,
    };
  }

  componentWillMount() {
    axios.get('http://localhost:3001/list')
    .then((response) => {
      const imgList = Object.defineProperties(response.data)
      this.setState({ imgList })
    })
    axios.get('http://localhost:3001/dimensions')
    .then((response) => {
      const imgSizes = Object.defineProperties(response.data)
      this.setState({ imgSizes })
    })
    window.addEventListener('resize', this.resize)
    this.resize()
  }

  resize = () => this.setState({ screenWidth: window.innerWidth})


