      (key, i) => <img width={'320px'} src={require(`../prenuvo_db/862625ef/${imgList[key]}/bw-gif.gif`)} key={i} alt={imgList[key]} />
    )
// this.imageOrganize()
    return (
      <div>
       <h1>MRI</h1> 
        <div>
          {images}
        </div>

      </div>
    )
  }
}
