import React, { Component } from 'react'
import '../sass/styles.css'
import Checkbox from './Checkbox';

const accept = ['I understand this']
const head_Symptoms = ['Frequent headaches', 'Frequent migraines' ]
const more_About_Headaches = ['Front', 'Back', 'Left Side', 'Right Side']
const frequency_Of_Headaches = ['Daily', '2/2 times a week', 'Weekly', 'Less frequent']
const throat_Neck_Esophegal_Symptoms = ['Coughing up Sputum', 'Irregular heartbeat (palpitations)','Shortness of breath without exercise']

const arrays = [
  accept, head_Symptoms, 
  more_About_Headaches, 
  frequency_Of_Headaches, 
  throat_Neck_Esophegal_Symptoms
]

export default class Form extends Component {
  state = {
    // accept : false,
    // generalSymtoms: {
    //   recentWeightGain: false,
    //   recentWeightLoss: false,
    // },
    // headSymptoms: 
    // Frequent_headaches: false,
  }
  componentWillMount = () => {
    this.selectedCheckboxes = new Set();
  }

  componentDidMount = () => {
    this.arraysToState()
  }

  arraysToState = () => {
    let stateList = []
    for(let array of arrays) {
      array.map(item => stateList.push(item.replace(/ /g, '_')))
    }
    let questionary = {}
    state
  }

  toggleCheckbox = label => {
    if (this.selectedCheckboxes.has(label)) {
      this.selectedCheckboxes.delete(label);
    } else {
      this.selectedCheckboxes.add(label);
    }
  }

  toggleCheckboxChange = (key) => {
    this.setState({ key: !this.state[key] || false })
    console.log('state', this.state[key])
  }

  createCheckbox = (label, type="checkbox") => {
    const key = label.replace(/ /g, '_')
    console.log(label)
    return (
      <span className="checkbox">
        <label>
          <input
              type={type}
              value={label}
              checked={!this.state[key]}
              onClick={type === 'radio' ? this.toggleCheckboxChange(key) : undefined}
              onChange={type === 'checkbox' ? this.toggleCheckboxChange(key) : undefined}
          />
          &nbsp;{label}
        </label>
      </span>
    )
  //   return (

  //   <Checkbox
  //     label={label}
  //     handleCheckboxChange={this.toggleCheckbox}
  //     key={label.replace(/ /g, '_')}
  //     type={type}
  // />
  // )
}

  render() {
    const { createCheckbox } = this
    return (
      <div className="form">
        <a href="#">Medical History &#62; Symptoms</a>
        <div>
          <h2>This section asks about any symptoms you have been experiencing lately</h2>
          <div>A note about the symptoms you are or have recently been feeling <span>*</span></div>
          <div>A Preuvo scan is a screen for cancer and many major diseases.</div>
          <div>The symptoms that you are feeling now or recently are important context for our review of the images that we acquire of your body.</div>
          <div>However, if you are feeling acute symptoms, it is important that you visit your family doctor to determine what is the best diagnostic approach that you
should follow. A Prenuvo scan can diagnose many conditions, can provide additional insight to a diagnosis but it is not always the most diagnostically
relevant approach for any one particular condition.</div>
        {createCheckbox('I understand this', 'radio')}
        <hr />
        <h3>General Symptoms</h3>
        <div>Check any of the following symptoms that you are or have recently experienced</div>
        {createCheckbox('Recent weight gain')}  {createCheckbox('Recent weight loss')}
        <hr />
        <h3>Head Symptoms</h3>
        <div>Check any of the following head symptoms that you are or have recently experienced</div>
        {head_Symptoms.map(label => createCheckbox(label))}

        {this.selectedCheckboxes.has('Frequent headaches') && <div>
          <h3>Tell us more about your headaches</h3>
        </div>}

        </div>

      </div>
    )
  }
}
