import React, { Component } from 'react';
import '../sass/styles.css';
import axios from 'axios';

// `../prenuvo_db/862625ef/${gridlist[key].path}/1361ae6e/bw-gif.gif`
// gridlist[key].path

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      imgList: [],
      imgSizes: [],
      screenWidth: 1140,
    };
  }

  componentWillMount() {
    axios.get('http://localhost:3001/list')
    .then((response) => {
      const imgList = Object.entries(response.data).map(item => item[1])
      this.setState({ imgList })
    })
    axios.get('http://localhost:3001/dimensions')
    .then((response) => {
      const imgSizes = Object.entries(response.data).map(item => item[1])
      this.setState({ imgSizes }, () => this.imageOrganize())
    })
    window.addEventListener('resize', this.resize)
    this.resize()

  }

  resize = () => this.setState({ screenWidth: window.innerWidth})

  imageOrganize = () => {
    const { imgList, imgSizes, screenWidth } = this.state
    let large = []
    let smallList = []
    let height = []
    let totalHeıght = 0
    let min = 0
    imgSizes.map((img, i) => {
      if(img.width > 480) {
        large.push(<img className="large_img" src={require(`../prenuvo_db/862625ef/${imgList[i]}/bw-gif.gif`)} key={i} alt={imgList[i]} />)
      } else {
        height.push(img.height) 
        totalHeıght += img.height
        min = min === 0 || min > img.height ? img.height : min
        smallList.push(imgList[i], img.height)
        // small.push(<img className="small_img" src={require(`../prenuvo_db/862625ef/${imgList[i]}/bw-gif.gif`)} key={i} alt={imgList[i]} />)
      }   
    })
    console.log('zzz', height)
    height = Math.ceil((height-min)/2) + min


    // this.setState({ large, height })
    console.log('xxx', large, smallList, height)
  }

  render() {
    const {  large, height } = this.state
    // const images = Object.keys(imgList).map(
    //   (key, i) => <img width={'320px'} src={require(`../prenuvo_db/862625ef/${imgList[key]}/bw-gif.gif`)} key={i} alt={imgList[key]} />
    // )

    return (
      <div className="img_page">
       <h1>MRI</h1> 
        <div width={'100%'} className="large">{large}</div>
        {/* <div  className="small">{small}</div> */}
      </div>
    )
  }
}
