import React, { Component } from 'react';
import '../sass/styles.css';
import gridlist from '../prenuvo_db/grid.json'
import axios from 'axios';
import { timingSafeEqual } from 'crypto';

// `../prenuvo_db/862625ef/${gridlist[key].path}/1361ae6e/bw-gif.gif`
// gridlist[key].path

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      imgList: {},
      imgSizes: {}
    };
  }

  componentWillMount() {
    axios.get('http://localhost:3001/list')
    .then((response) => {
      this.setState({ imgList: response.data})
    })
    axios.get('http://localhost:3001/dimensions')
    .then((response) => {
      this.setState({ imgSizes: response.data})
    })
    window.addEventListener('resize', this.resize)
  }

  resize = () => this.setState({ screenWidth: innerWidth})

  onImgLoad = ({ target: img }) => {
    const { imgSizes } = this.state
    const obj = {}
    obj[img.alt] = {
        width: img.width,
        height: img.height,
      }
    imgSizes.push(obj)
    this.setState({
      imgSizes
    });
    // console.log('img', obj, imgSizes, img.alt)
  }

  onImgError = e => {
    e.target.src = '/path/no-picture.jpg';
    this.setState({
      error: true,
    });
  }

  render() {
    const { imgList, imgSizes, screenWidth } = this.state
 console.log(screenWidth)
    const images = Object.keys(imgList).map(
      (key, i) => <img src={require(`../prenuvo_db/862625ef/${imgList[key]}/bw-gif.gif`)} key={i} alt={imgList[key]} />
    )

    return (
      <div>
       <h1>MRI</h1> 
        <div>
          {images}
        </div>

      </div>
    )
  }
}
