import React, { Component } from 'react';
import '../sass/styles.css';
import gridlist from '../prenuvo_db/grid.json'


// `../prenuvo_db/862625ef/${gridlist[key].path}/1361ae6e/bw-gif.gif`
// gridlist[key].path

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      imgList: [],
      imgSizes: [],
    };
  }

  componentWillMount() {
    const keys = Object.keys(gridlist)
    const imagesFolderNames = keys.map(key => gridlist[key].path)
    const imgList = imagesFolderNames.map(
      (path, i) => <img src={require(`../prenuvo_db/862625ef/${path}/bw-gif.gif`)} onLoad={this.onImgLoad} refs={path} key={i} alt={path} />
      )
    this.setState({ imgList })
    console.log(imgList)
  }

  onImgLoad = ({ target: img }) => {
    console.log('img', img.alt)
    this.setState({
      width: img.width,
      height: img.height,
    });
  }

  onImgError = e => {
    e.target.src = '/path/no-picture.jpg';
    this.setState({
      error: true,
    });
  }

  render() {
    const { width, height, error } = this.state;
    console.log(width, height, error);
    console.log(gridlist)

    // const imgList = this.state.imagesFolderNames.map(
    //   (path, i) => <img src={require(`../prenuvo_db/862625ef/${path}/bw-gif.gif`)} onLoad={this.onImgLoad} key={i} alt={path} />
    //   )

    return (
      <div>
       <h1>IMAGES</h1> 
        <div>
          {this.state.imgList}
        </div>

      </div>
    )
  }
}
